mapper.update:
update tb_user_b
set 	u_b_pw = #{u_b_pw},
        u_b_number = #{u_b_number},
        u_b_email = #{u_b_email}
where u_b_id = #{u_b_id}

update tb_user_b 
set u_b_access = '9999'
where u_b_id = #{u_b_id}

 update tb_store
 set s_regi_num = #{s_regi_num},
         s_name = #{s_name},
         s_number = #{s_number},
         s_state = #{s_state},
         s_time = #{s_time},
         u_b_img_upload = #{u_b_img_upload},
         s_desc = #{s_desc}
where u_b_id = #{u_b_id} 
and u_s_id = #{u_s_id}

mapper.select:
select u_p_id,
		p_memo
from tb_p_memo
where u_s_id = #{u_s_id}
and u_p_id = #{u_p_id}

mapper.update:
update tb_p_memo
set p_memo = #{p_memo}
where u_s_id = #{u_s_id}
and u_p_id = #{u_p_id}
