mapper.select:
select *
from tb_user_p
where u_p_id=#{u_p_id} 
and   u_p_pw=#{u_p_pw}

select * 
from tb_user_b
where u_b_id =#{u_b_id}
and u_b_pw =#{u_b_pw}

select u_p_id
from tb_user_p
where u_p_name =#{u_p_name}
and u_p_number=#{u_p_number}

select u_b_id
from tb_user_b
where u_b_name =#{u_b_name}
and u_b_number=#{u_b_number}

select u_p_pw
from tb_user_p
where u_p_id =#{u_p_id}
and u_p_name =#{u_p_name} 
and u_p_number=${u_p_number}

select u_b_pw
from tb_user_b
where u_b_id =#{u_b_id}
and u_b_name =#{u_b_name}
and u_b_number=#{u_b_number}
