mapper.select:
		select u_p_id,
		u_s_id,
		z_desc
        from tb_zzim 
        where u_p_id = #{u_p_id} 
        and u_s_id= #{u_s_id}

mapper.update:
update tb_zzim
set
z_desc = #{z_desc}
where u_p_id = #{u_p_id}
and u_s_id = #{u_s_id}

mapper.delete:
delete from foodb.tb_zzim
where u_p_id = #{u_p_id} 
and u_s_id = #{u_s_id}
